bindToggle.body:
var self = this;
    self.unbindToggle();

    $("canvas").on("keyup.cameraToggler", function(e)
    {
        var camList = _dView.getCameraList()[1];
        var camera = _dView.getCamera().parent;
        var toCamera;
        var key = self.keyCode ? self.keyCode.toUpperCase() : " ";
        var sender = self.Scene.children_by_name.xAPISender;

        //If necessary, remove the editor cam from camList
        if (!self.cycleEditor && !camList[0]) camList.splice(0, 1);

        //If the toggle button has been pressed and there are at least two cams in camList
        if (camList.length >= 2 && e.keyCode === key.charCodeAt(0))
        {
            if (camera)
            {
                var index = (camList.indexOf(camera.vwfID) + 1) % camList.length;
                toCamera = camList[index];
            }

            //If camera is falsy, then the editor cam is assumed active (camList[0])
            else toCamera = camList[1];

            _dView.setCamera(toCamera);

            if (sender)
            {
                sender.xAPISend("observed", (toCamera || "editorCamera"), "Instructor", null);
            }
        }

        //Else, handle numpad and numbers row presses
        else if (self.bindNumbers)
        {
            var numPad = e.keyCode - 96;
            var numRow = e.keyCode - 48;
            if (numPad >= 0 && numPad <= 9 && numPad < camList.length)
            {
                _dView.setCamera(camList[numPad]);

                if ((!camera && camList[numPad]) || (camera && camera.vwfID !== camList[numPad]))
                {
                    if (sender)
                    {
                        sender.xAPISend("observed", (camList[numPad] || "editorCamera"), "Instructor", null);
                    }
                }
            }
            else if (numRow >= 0 && numRow <= 9 && numRow < camList.length)
            {
                _dView.setCamera(camList[numRow]);

                if ((!camera && camList[numRow]) || (camera && camera.vwfID !== camList[numRow]))
                {
                    if (sender)
                    {

                        sender.xAPISend("observed", (camList[numRow] || "editorCamera"), "Instructor", null);
                    }
                }
            }
        }
    });
